const activeLink = ref('allmeals');

function updateActiveLink(category) {
    activeLink.value = category;
}

function handleMouseOver(event){
    console.log('mouseover', event);
    if (event.target.classList.contains('icon-link')) {
        event.target.classList.add('hovering');
        // Play all SVG animations inside the hovered link
        event.target.querySelectorAll('svg').forEach(svg => {
        svg.getAnimations({subtree: true}).forEach(anim => anim.play());
    });
    }
}

function handleMouseLeave(event){
